import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Navbar from "./components/navbar";
import AddClient from "./components/AddClient";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if the user is authenticated when the app mounts
  useEffect(() => {
    const authStatus = localStorage.getItem("isAuthenticated");
    if (authStatus === "true") {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <>
      {isAuthenticated && <Navbar />} {/* Always show Navbar if authenticated */}
      <Routes>
        <Route
          path="/"
          element={<Login setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/home"
          element={isAuthenticated ? <div>Home Content</div> : <Navigate to="/" replace />}
        />
        <Route
          path="/addclient"
          element={isAuthenticated ? <AddClient /> : <Navigate to="/" replace />}
        />
      </Routes>
    </>
  );
};

export default function MainApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
