import React, { useState } from "react";
import NewCustomerForm from "./NewCustomerForm"; // Import the NewCustomerForm

const AddClient = () => {
  const [open, setOpen] = useState(false);

  // Handler to open the form dialog
  const handleOpen = () => setOpen(true);

  // Handler to close the form dialog
  const handleClose = () => setOpen(false);

  // Save customer data handler (you can add your own logic here)
  const handleSaveCustomer = (customer) => {
    console.log("New Customer Saved:", customer);
    // Add your save logic here, like API calls or state management
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">Add Client</h1>
      <form className="mt-4 space-y-4">
        <div>
          <label className="block text-gray-700">Client Name</label>
          <input
            type="text"
            className="w-3/4 p-3 border rounded-md"
          />
        </div>
        <div>
          <label className="block text-gray-700">Email</label>
          <input
            type="email"
            className="w-3/4 p-3 border rounded-md"
          />
        </div>
        <div>
          <label className="block text-gray-700">Phone</label>
          <input
            type="tel"
            className="w-3/4 p-3 border rounded-md"
          />
        </div>
        <div>
          <label className="block text-gray-700">Address</label>
          <input
            type="text"
            className="w-3/4 p-3 border rounded-md"
          />
        </div>
        <button
          type="button"
          onClick={handleOpen} // Open the NewCustomerForm dialog
          className="px-6 py-3 bg-blue-500 text-white rounded-md mt-4"
        >
          Add Client
        </button>
      </form>

    </div>
  );
};

export default AddClient;
