import React from "react";

const Dashboard = () => {
  const cards = [
    { title: "Design Engineering", proposals: 12, orders: 7 },
    { title: "PMC", proposals: 10, orders: 8 },
    { title: "Pre ", proposals: 12, orders: 7 },
    { title: "Design Engineering", proposals: 12, orders: 7 },
    { title: "Design Engineering", proposals: 12, orders: 7 },
    { title: "Design Engineering", proposals: 12, orders: 7 },
  ];

  return (
    <div className="min-h-screen flex justify-center items-center">
      <div className="grid grid-cols-3 gap-12">
        {cards.map((card, index) => (
          <div
            key={index}
            className="bg-gradient-to-r from-blue-100 to-blue-200 rounded-2xl shadow-lg p-8 w-72 h-48 flex flex-col justify-between transform transition-transform duration-300 hover:-translate-y-1 hover:shadow-2xl"
          >
            <h2 className="text-center text-xl font-bold mb-4">{card.title}</h2>
            <div className="grid grid-cols-2 divide-x divide-gray-400">
              <div className="text-center">
                <p className="font-medium">Proposals:</p>
                <p>{card.proposals}</p>
              </div>
              <div className="text-center">
                <p className="font-medium">Orders:</p>
                <p>{card.orders}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
